mapper.select:


		select * from(  		
			select rownum as rn, replyId, bookId, memberId, content, rating, regDate
			from vam_reply
			where rownum <= #{pageNum} * #{amount}  and bookId = #{bookId}	
			order by regDate desc  	
  			)
  		where rn > (#{pageNum} -1) * #{amount}


select count(*)
from vam_reply
where bookId = #{bookId}


select avg(rating)
from vam_reply
where bookId = #{bookId}

mapper.update:

update vam_book
set ratingAvg = #{ratingAvg}
where bookId = #{bookId}
